/*
Module 1 avec partage des methodes et variables:
sayHello(), sayHi() et hello
*/
sayHello = () => {
    console.log('Hello !')
}

sayHi = () => {
    console.log('Hi !')
}

hello = "Hello world"

// Gestion des methodes et variables reellement partagees
exports.sayHello = sayHello
exports.sayHi = sayHi
exports.hello = hello